public static void fe_mul(int[] h,int[] f,int[] g)
{

  long hr[] = fe_mul1(f, g);

  long carry0;
  long carry1;
  long carry2;
  long carry3;
  long carry4;
  long carry5;
  long carry6;
  long carry7;
  long carry8;
  long carry9;

  /*
