import hashlib
import io

class Sha256File:
    def __init__(self, fo, hashvalue):
        self._fo = fo
        self.hashvalue = hashvalue

    def __enter__(self):
        self._m = hashlib.sha256()
        return self

    def read(self, size=-1):
        b = self._fo.read(size)
        self._m.update(b)
        return b

    def __iter__(self):
        return self

    def __next__(self):
        b = self.read()
        if b == b'':
            raise StopIteration
        return b

    def __exit__(self, exc_type, exc_value, traceback):
        if exc_type is None:
            assert self._m.hexdigest() == self.hashvalue
        self._fo.close()

